import { getList, changeStatus, add, edit, del, getHash, refresh } from '@/api/interface'
import { getAll } from '@/api/interface-group'

const editButton = (vm, h, currentRow, index) => {
  if (vm.buttonShow.edit) {
    return h('Button', {
      props: {
        type: 'primary'
      },
      style: {
        margin: '0 5px'
      },
      on: {
        'click': () => {
          vm.formItem.id = currentRow.id
          vm.formItem.api_class = currentRow.api_class
          vm.formItem.info = currentRow.info
          vm.formItem.method = currentRow.method
          vm.formItem.hash = currentRow.hash
          vm.formItem.group_hash = currentRow.group_hash
          vm.formItem.access_token = currentRow.access_token
          vm.formItem.is_test = currentRow.is_test
          vm.formItem.hash_type = currentRow.hash_type
          vm.modalSetting.show = true
          vm.modalSetting.index = index
        }
      }
    }, vm.$t('edit_button'))
  }
}
const deleteButton = (vm, h, currentRow, index) => {
  if (vm.buttonShow.del) {
    return h('Poptip', {
      props: {
        confirm: true,
        title: '您确定要删除这条数据吗? ',
        transfer: true
      },
      on: {
        'on-ok': () => {
          del(currentRow.hash).then(response => {
            currentRow.loading = false
            vm.tableData.splice(index, 1)
            vm.$Message.success(response.data.msg)
          })
        }
      }
    }, [
      h('Button', {
        style: {
          margin: '0 5px'
        },
        props: {
          type: 'error',
          placement: 'top',
          loading: currentRow.isDeleting
        }
      }, vm.$t('delete_button'))
    ])
  }
}
const requestButton = (vm, h, currentRow, index) => {
  if (vm.buttonShow.request) {
    return h('Button', {
      style: {
        margin: '0 5px'
      },
      props: {
        type: 'info',
        placement: 'top',
        loading: currentRow.isDeleting
      },
      on: {
        click: () => {
          let hash = currentRow.hash
          vm.$router.push({
            path: `request/${hash}`
          })
        }
      }
    }, '请求参数')
  }
}
const responseButton = (vm, h, currentRow, index) => {
  if (vm.buttonShow.response) {
    return h('Button', {
      style: {
        margin: '0 5px'
      },
      props: {
        type: 'warning',
        placement: 'top',
        loading: currentRow.isDeleting
      },
      on: {
        click: () => {
          let hash = currentRow.hash
          vm.$router.push({
            path: `response/${hash}`
          })
        }
      }
    }, '返回参数')
  }
}

export default {
  name: 'interface_list',
  data () {
    return {
      confirmRefresh: false,
      refreshLoading: false,
      columnsList: [
        {
          title: '序号',
          type: 'index',
          width: 65,
          align: 'center'
        },
        {
          title: '接口名称',
          align: 'center',
          minWidth: 190,
          key: 'info'
        },
        {
          title: '真实类库',
          align: 'center',
          key: 'api_class',
          width: 190
        },
        {
          title: '接口映射',
          align: 'center',
          key: 'hash',
          width: 140
        },
        {
          title: '请求方式',
          align: 'center',
          width: 95,
          render: (h, params) => {
            if (params.row.isTest === 1) {
              return h('tag', {
                attrs: {
                  color: 'error'
                }
              }, '测试')
            } else {
              switch (params.row.method) {
                case 1:
                  return h('Tag', {
                    attrs: {
                      color: 'success'
                    }
                  }, 'POST')
                case 2:
                  return h('Tag', {
                    attrs: {
                      color: 'primary'
                    }
                  }, 'GET')
                case 0:
                  return h('Tag', {
                    attrs: {
                      color: 'warning'
                    }
                  }, '不限')
              }
            }
          }
        },
        {
          title: '接口状态',
          align: 'center',
          width: 130,
          render: (h, params) => {
            let vm = this
            return h('i-switch', {
              attrs: {
                size: 'large'
              },
              props: {
                'true-value': 1,
                'false-value': 0,
                value: params.row.status,
                disabled: !vm.buttonShow.changeStatus
              },
              on: {
                'on-change': function (status) {
                  changeStatus(status, params.row.hash).then(response => {
                    vm.$Message.success(response.data.msg)
                    vm.getList()
                  })
                }
              }
            }, [
              h('span', {
                slot: 'open'
              }, vm.$t('open_choose')),
              h('span', {
                slot: 'close'
              }, vm.$t('close_choose'))
            ])
          }
        },
        {
          title: '操作',
          align: 'center',
          minWidth: 375,
          render: (h, params) => {
            return h('div', [
              editButton(this, h, params.row, params.index),
              requestButton(this, h, params.row, params.index),
              responseButton(this, h, params.row, params.index),
              deleteButton(this, h, params.row, params.index)
            ])
          }
        }
      ],
      tableData: [],
      apiGroup: [],
      tableShow: {
        currentPage: 1,
        pageSize: 10,
        listCount: 0
      },
      searchConf: {
        type: '',
        keywords: '',
        status: ''
      },
      modalSetting: {
        show: false,
        loading: false,
        index: 0
      },
      formItem: {
        api_class: '',
        info: '',
        group_hash: 'default',
        method: 2,
        hash_type: 2,
        hash: '',
        access_token: 0,
        is_test: 0,
        id: 0
      },
      ruleValidate: {
        api_class: [
          { required: true, message: '真实类库不能为空', trigger: 'blur' }
        ],
        info: [
          { required: true, message: '接口名称不能为空', trigger: 'blur' }
        ]
      },
      buttonShow: {
        edit: true,
        request: true,
        response: true,
        del: true,
        changeStatus: true
      },
      listLoading: false
    }
  },
  created () {
    let vm = this
    vm.getList()
    vm.hasRule('InterfaceList/edit').then(res => {
      vm.buttonShow.edit = res
    })
    vm.hasRule('InterfaceList/del').then(res => {
      vm.buttonShow.del = res
    })
    vm.hasRule('InterfaceList/changeStatus').then(res => {
      vm.buttonShow.changeStatus = res
    })
    vm.hasRule('Fields/response').then(res => {
      vm.buttonShow.response = res
    })
    vm.hasRule('Fields/request').then(res => {
      vm.buttonShow.request = res
    })
    getAll().then(response => {
      vm.apiGroup = response.data.data.list
    })
  },
  activated () {
    let vm = this
    getAll().then(response => {
      vm.apiGroup = response.data.data.list
    })
  },
  methods: {
    alertAdd () {
      let vm = this
      getHash().then(response => {
        vm.formItem.hash = response.data.data.hash
      })
      vm.modalSetting.show = true
    },
    submit () {
      let vm = this
      this.$refs['myForm'].validate((valid) => {
        if (valid) {
          vm.modalSetting.loading = true
          if (vm.formItem.id === 0) {
            add(vm.formItem).then(response => {
              vm.modalSetting.loading = false
              vm.$Message.success(response.data.msg)
              vm.getList()
              vm.cancel()
            }).catch(() => {
              vm.modalSetting.loading = false
            })
          } else {
            edit(vm.formItem).then(response => {
              vm.modalSetting.loading = false
              vm.$Message.success(response.data.msg)
              vm.getList()
              vm.cancel()
            }).catch(() => {
              vm.modalSetting.loading = false
            })
          }
        }
      })
    },
    cancel () {
      this.modalSetting.show = false
    },
    changePage (page) {
      this.tableShow.currentPage = page
      this.getList()
    },
    changeSize (size) {
      this.tableShow.pageSize = size
      this.getList()
    },
    search () {
      this.tableShow.currentPage = 1
      this.getList()
    },
    getList () {
      let vm = this
      vm.listLoading = true
      getList({
        page: vm.tableShow.currentPage,
        size: vm.tableShow.pageSize,
        type: vm.searchConf.type,
        keywords: vm.searchConf.keywords,
        status: vm.searchConf.status
      }).then(response => {
        vm.tableData = response.data.data.list
        vm.tableShow.listCount = response.data.data.count
        vm.listLoading = false
      })
    },
    doCancel (data) {
      if (!data) {
        this.formItem.id = 0
        this.$refs['myForm'].resetFields()
        this.modalSetting.loading = false
        this.modalSetting.index = 0
      }
    },
    refreshRoute () {
      let vm = this
      vm.refreshLoading = true
      refresh().then(response => {
        vm.$Message.success(response.data.msg)
        vm.confirmRefresh = false
        vm.refreshLoading = false
      }).catch(() => {
        vm.confirmRefresh = false
        vm.refreshLoading = false
      })
    }
  }
}
