import { getRequest, add, edit, del } from '@/api/fields'

const editButton = (vm, h, currentRow, index) => {
  return h('Button', {
    props: {
      type: 'primary'
    },
    style: {
      margin: '0 5px'
    },
    on: {
      'click': () => {
        vm.formItem.id = currentRow.id
        vm.formItem.field_name = currentRow.field_name
        vm.formItem.data_type = currentRow.data_type.toString()
        vm.formItem.defaults = currentRow.default
        vm.formItem.range = currentRow.range
        vm.formItem.is_must = currentRow.is_must.toString()
        vm.formItem.info = currentRow.info
        vm.modalSetting.show = true
        vm.modalSetting.index = index
      }
    }
  }, vm.$t('edit_button'))
}
const deleteButton = (vm, h, currentRow, index) => {
  return h('Poptip', {
    props: {
      confirm: true,
      title: '您确定要删除这条数据吗? ',
      transfer: true
    },
    on: {
      'on-ok': () => {
        del(currentRow.id).then(response => {
          currentRow.loading = false
          vm.tableData.splice(index, 1)
          vm.$Message.success(response.data.msg)
        })
      }
    }
  }, [
    h('Button', {
      style: {
        margin: '0 5px'
      },
      props: {
        type: 'error',
        placement: 'top',
        loading: currentRow.isDeleting
      }
    }, vm.$t('delete_button'))
  ])
}
export default {
  name: 'interface_request',
  data () {
    return {
      hash: '',
      columnsList: [
        {
          title: '序号',
          type: 'index',
          width: 65,
          align: 'center'
        },
        {
          title: '字段名称',
          align: 'left',
          key: 'show_name',
          width: 200
        },
        {
          title: '数据类型',
          align: 'center',
          width: 100,
          render: (h, params) => {
            let type = params.row.data_type
            return h('Tag', {
              attrs: {
                color: 'primary'
              }
            }, this.tableShow.dataType[type])
          }
        },
        {
          title: '是否必须',
          align: 'center',
          width: 100,
          render: (h, params) => {
            if (params.row.is_must === 1) {
              return h('Tag', {
                attrs: {
                  color: 'error'
                }
              }, '必填')
            } else {
              return h('Tag', {
                attrs: {
                  color: 'primary'
                }
              }, '可选')
            }
          }
        },
        {
          title: '默认值',
          align: 'center',
          key: 'default',
          width: 160
        },
        {
          title: '字段说明',
          align: 'left',
          key: 'info'
        },
        {
          title: '操作',
          align: 'center',
          width: 205,
          render: (h, params) => {
            return h('div', [
              editButton(this, h, params.row, params.index),
              deleteButton(this, h, params.row, params.index)
            ])
          }
        }
      ],
      tableData: [],
      tableShow: {
        currentPage: 1,
        pageSize: 10,
        listCount: 0,
        dataType: {}
      },
      apiInfo: {},
      modalSetting: {
        show: false,
        loading: false,
        index: 0
      },
      formItem: {
        field_name: '',
        data_type: '2',
        defaults: '',
        range: '',
        is_must: '1',
        info: '',
        type: 0,
        id: 0
      },
      ruleValidate: {
        field_name: [
          { required: true, message: '字段名称不能为空', trigger: 'blur' }
        ]
      },
      listLoading: false
    }
  },
  created () {
    this.hash = this.$route.params.hash.toString()
    this.getList()
  },
  activated () {
    this.hash = this.$route.params.hash.toString()
    this.getList()
  },
  methods: {
    alertAdd () {
      this.modalSetting.show = true
    },
    submit () {
      this.formItem.hash = this.hash
      let vm = this
      this.$refs['myForm'].validate((valid) => {
        if (valid) {
          vm.modalSetting.loading = true
          if (vm.formItem.id === 0) {
            add(vm.formItem).then(response => {
              vm.$Message.success(response.data.msg)
              vm.getList()
              vm.cancel()
            }).catch(() => {
              vm.modalSetting.loading = false
            })
          } else {
            edit(vm.formItem).then(response => {
              vm.$Message.success(response.data.msg)
              vm.getList()
              vm.cancel()
            }).catch(() => {
              vm.modalSetting.loading = false
            })
          }
        }
      })
    },
    cancel () {
      this.modalSetting.show = false
    },
    changePage (page) {
      this.tableShow.currentPage = page
      this.getList()
    },
    changeSize (size) {
      this.tableShow.pageSize = size
      this.getList()
    },
    search () {
      this.tableShow.currentPage = 1
      this.getList()
    },
    getList () {
      let vm = this
      vm.listLoading = true
      getRequest({
        page: vm.tableShow.currentPage,
        size: vm.tableShow.pageSize,
        hash: vm.hash
      }).then(response => {
        vm.tableData = response.data.data.list
        vm.tableShow.listCount = response.data.data.count
        vm.tableShow.dataType = response.data.data.dataType
        vm.apiInfo = response.data.data.apiInfo
        vm.listLoading = false
      })
    },
    doCancel (data) {
      if (!data) {
        this.formItem.id = 0
        this.formItem.defaults = ''
        this.formItem.isMust = '1'
        this.$refs['myForm'].resetFields()
        this.modalSetting.loading = false
        this.modalSetting.index = 0
      }
    }
  }
}
